sp_widget.client_script:
api.controller = function($scope) {
  var c = this;
  c.task = {};

  c.assignTask = function() {
    c.server.get({ task: c.task }).then(function(response) {
      c.success = response.data.success;
    });
  };
};

sp_widget.script:
(function() {
  data.success = '';

  if (input && input.task && input.task.task_name && input.task.event_id) {
    var eventGR = new GlideRecord('x_1801001_event_ma_event');
    if (eventGR.get('number', input.task.event_id)) {
      var userGR = new GlideRecord('sys_user');
      userGR.addQuery('user_name', input.task.assigned_to);
      userGR.query();
      if (userGR.next()) {
        var taskGR = new GlideRecord('x_1801001_event_ma_task');
        taskGR.initialize();
        taskGR.setValue('x_task_name', input.task.task_name);
        taskGR.setValue('x_due_date', input.task.due_date);
        taskGR.setValue('x_status', input.task.status);
        taskGR.setValue('assigned_to', userGR.getUniqueValue());
        taskGR.setValue('x_event_id', eventGR.getUniqueValue());
        taskGR.insert();
        data.success = 'Task assigned successfully.';
      } else {
        data.success = 'Invalid Username.';
      }
    } else {
      data.success = 'Invalid Event ID.';
    }
  }
})();
