sp_widget.client_script:
api.controller = function($scope, $timeout) {
  var c = this;
  c.success = null;
  c.error = null;

  c.view = function(id) {
    alert("View full feedback not implemented yet.\nYou clicked ID: " + id);
  };

  c.delete = function(id) {
    if (confirm("Are you sure you want to delete this feedback?")) {
      c.server.get({ action: 'delete', id: id }).then(function(response) {
        c.success = response.data.success;
        c.error = response.data.error;
        c.reload();
      });
    }
  };

  c.reload = function() {
    $timeout(function() {
      c.server.get().then(function(response) {
        $scope.data = response.data;
      });
    }, 400);
  };
};

sp_widget.script:
(function() {
  data.feedbacks = [];
  data.success = '';
  data.error = '';

  if (input && input.action && input.id) {
    var fb = new GlideRecord('x_1801001_event_ma_feedback');
    if (fb.get(input.id)) {
      if (input.action === 'delete') {
        fb.deleteRecord();
        data.success = 'Feedback deleted successfully.';
      }
    } else {
      data.error = 'Feedback not found.';
    }
  }

  var gr = new GlideRecord('x_1801001_event_ma_feedback');
  gr.orderByDesc('sys_created_on');
  gr.query();
  while (gr.next()) {
    data.feedbacks.push({
      sys_id: gr.getUniqueValue(),
      number: gr.getValue('number'),
      event: gr.getDisplayValue('x_event_id'),
      attendee: gr.getDisplayValue('x_attendee_id'),
      rating: gr.getValue('x_rating'),
      comments: gr.getValue('x_comments')
    });
  }
})();
