function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (g_form.getValue('x_rating') == 'good') {
        g_form.addInfoMessage('thank you!!');
    } else if (g_form.getValue('x_rating') == 'excellent') {
        g_form.addInfoMessage('Thank you for your acknowledgement');
    } else if (g_form.getValue('x_rating') == 'average') {
        g_form.addInfoMessage('Should try and Improve');
    } else if (g_form.getValue('x_rating') == 'poor') {
        g_form.addInfoMessage("We are sorry");
    }

    //Type appropriate comment here, and begin script below

}
