sys_script_include.script:
var AttendeeUtils = Class.create();
AttendeeUtils.prototype = {
    initialize: function() {
		this.attendees = new GlideRecord('x_1801001_event_ma_attendee');
    },

	checkAttendeeIfRegistered : function(attId){
		if(this.attendees.get(attId)){
			return true;
		}
		else{
			return false;
		}
	},

	fetchAttendeeById : function(attId){
		if (this.attendees.get(attId)){
			var attendee_details = {
				att_num : this.attendees.getValue('number'),
				att_event_id : this.attendees.getValue('x_event_id'),
				att_first_name : this.attendees.getValue('x_first_name'),
				att_last_name : this.attendees.getValue('x_last_name'),
				att_email : this.attendees.getValue('x_email'),
				att_contact_number : this.attendees.getValue('x_contact_number')
			};	

			return attendee_details;
		}
		else{
			return null;
		}
	},

	fetchAttendeesByEventId :function(eventId){
		this.attendees.addQuery('x_event_id', eventId);
		this.attendees.query();
		return this.attendees;
	},



    type: 'AttendeeUtils'
};
