sys_script_include.script:
var RescheduleEventUtils = Class.create();
RescheduleEventUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	reschedule: function()
	{
		var sysId = this.getParameter("sysparm_sys_id");
		var newDate = this.getParameter("sysparm_new_date");

		var gr = new GlideRecord("x_1801001_event_ma_event");
		gr.addQuery("sys_id", sysId);
		gr.query();
		if (gr.next()) {
		   gr.x_event_date = newDate;
		   gr.update();
		   gs.eventQueue("x_1801001_event_ma.rescheduled.event", gr, sysId, newDate);
		}


	},

    type: 'RescheduleEventUtils'
});
