sys_script_include.script:
var CancelTaskUtils = Class.create();
CancelTaskUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	cancelTask: function() {
    var sysId = this.getParameter('sysparm_sys_id');

    var gr = new GlideRecord('x_1801001_event_ma_task');    
	if (gr.get(sysId)) {
      gr.x_status = 'cancelled';
      gr.update();

      if (gr.assigned_to) {
        gs.eventQueue('x_1801001_event_ma.task.cancelled', gr, gr.assigned_to.toString(), '');
      }

      return 'success';
    }
    return 'failure';
  },

    type: 'CancelTaskUtils'
});
