sys_script_include.script:
var BudgetUtils = Class.create();
BudgetUtils.prototype = {
    initialize: function() {
		this.bud = new GlideRecord('x_1801001_event_ma_budget');
		this.eventRecord = new GlideRecord('x_1801001_event_ma_event');
    },

	calculateTotalBudget : function(eventId){
		if(!this.eventRecord.get(eventId)){
			return null;
		}
		this.bud.query('x_event_id', eventId);
		this.bud.query();
		var total_amount = 0;
		while(this.bud.next()){
			total_amount += ParseFloat(this.bud.getValue('x_total_amount'));
		}

		return total_amount;
	},

	isBudgetApproved : function(budId){
		if(this.bud.get(budId)){
			if(this.bud.getValue('x_approval_status') == true){
				return true;
			}
			else{
				return false;
			}
		}

	},

	getBudgetDetailsByEventId : function(evenId){
		this.bud.addQuery('x_event_id', eventId);
		this.bud.query();
		var budgets = [];
		while(this.bud.next()){
			budgets.push({
				bud_num: this.bud.getValue('number'),
				bud_tot_am: this.bud.getValue('x_total_amount'),
				bud_am_sp: this.bud.getValue('x_amount_spent'),
				bud_remain: this.bud.getValue('x_amount_remaining'),
				bud_sys_id: this.bud.getUniqueValue().toString(),
				bud_app_stat : this.bud.getValue('x_approval_status')
			});
		}
		return budgets;
	},


	calculateRemainingBudget : function(bId){
		if(this.bud.get(bId)){
			var remaining_amount = parseFloat(this.bud.getValue('x_total_budget')) - parseFloat(this.bud.getValue('x_amount_spent'));
			return remaining_amount;
		}
	},

    type: 'BudgetUtils'
};
