sys_script_include.script:
var EventUtilsClient = Class.create();
EventUtilsClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	fetchEventDetailsById: function () {
		var eventId = this.getParameter('sysparm_event_id');
		var eventRecGr = new GlideRecord('x_1801001_event_ma_event');
		if (eventRecGr.get(eventId)) {
			var results = {
				eve_sys_id: eventId,
				eve_num: eventRecGr.getValue('number'),
				eve_name: eventRecGr.getValue('x_event_name'),
				eve_date: eventRecGr.getValue('x_event_date'),
				eve_loc: eventRecGr.getDisplayValue('x_location'),
				eve_stat: eventRecGr.getValue('x_status'),
				eve_cont_pers: eventRecGr.getValue('x_contact_person'),
				eve_cont_num: eventRecGr.getValue('x_contact_number'),
				eve_email: eventRecGr.getValue('x_email'),

			};
			return JSON.stringify(results);
		}
		else {
			return null;
		}

	},

	checkIfVip: function () {
		var eventID = this.getParameter('sysparm_event_id');
		var eventRecGr = new GlideRecord('x_1801001_event_ma_event');
		eventRecGr.get(eventID);
		if (eventRecGr.x_vip == true) {
			return 'VIP';
		}
		else {
			return 'Non-VIP';
		}

	},

	checkLocation: function () {
		var locId = this.getParameter('sysparm_location_id');
		var locationGr = new GlideRecord('x_1801001_event_ma_location');
		locationGr.get(locId);
		if (locationGr.x_available == true) {
			return 'true';
		}
		else {
			return 'false';
		}
	},
	type: 'EventUtilsClient'
});
