sys_script_include.script:
var FeedbackUtils = Class.create();
FeedbackUtils.prototype = {
    initialize: function() {
		this.feedback = new GlideRecord('x_1801001_event_ma_feedback');
    },
	fetchFeedbackDetailsById : function(fId){
		if(this.feedback.get(fId)){
			var results = {
				f_number : this.feedback.getValue('number'),
				f_event_id : this.feedback.getValue('x_event_id'),
				f_att_id : this.feedback.getValue('x_attendee_id'),
				f_rating : this.feedback.getValue('x_rating'),
				f_comments : this.feedback.getValue('x_comments')

			}

			return results;
		}
		else{
			return null;
		}
	},

	checkIfPositiveFeeback : function(fId){
		if(this.feedback.get(fId)){
			if(this.feedback.getValue('x_rating') == 'excellent' || this.feedback.getValue('x_rating') == 'good'){
				return true;
			}
			else{
				return false;
			}
		}
	},

	getFeedbackByEvent: function(eventId) {
        var feedbackList = [];
        this.feedback.addQuery('x_event_id', eventId);
        fbGR.query();
        while (this.feedback.next()) {
            feedbackList.push({
                f_sys_id: this.feedback.getUniqueValue(),
                f_rating: this.feedback.getValue('x_rating'),
                f_comments: this.feedback.getValue('x_comments'),
            });
        }
        return feedbackList;
    },


    type: 'FeedbackUtils'
};
