sys_script_include.script:
var VendorUtils = Class.create();
VendorUtils.prototype = {
    initialize: function() {
		this.vendor = new GlideRecord('x_1801001_event_ma_vendor');
    },

	getVendorsById : function(venId){
		if(this.vendor.get(venId)){
			var results = {
				ven_id : venId,
				ven_event_id : this.vendor.getValue('x_event_id'),
				ven_name : this.vendor.getValue('x_vendor_name'),
				ven_contact_number : this.vendor.getValue('x_contact_number'),
				ven_email :  this.vendor.getValue('x_email')
			};
			return results;
		}
		else{
			return null;
		}
	},

	checkIfAvailable : function(venId){
		if(this.bud.get(venId)){
			if(this.bud.getValue('x_available') == true){
				return true;
			}
			else{
				return false;
			}
		}
	},

	fetchVendorsByEventId : function(eventId){
		this.vendor.addQuery('x_event_id', eventId);
		this.vendor.query();
		var eventVendors = [];
		while(this.vendor.next()){
			eventVendors.push({
				ven_id : this.vendor.getUniqueValue(),
				ven_name : this.vendor.getValue('x_vendor_name'),
				ven_contact_number : this.vendor.getValue('x_contact_number'),
				ven_email :  this.vendor.getValue('x_email')

			});
		}
		return eventVendors;

	},

    type: 'VendorUtils'
};
