sys_script_include.script:
var AttendeeUtilsClient = Class.create();
AttendeeUtilsClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	fetchAttendeeDetailsById : function(){
		var attendeeId = this.getParameter('sysparm_attendee_id');
		var attendeeGr = new GlideRecord('x_1801001_event_ma_attendee');
		if(attendeeGr.get(attendeeId)){
			var results = {
				attendee_sys_id : attendeeId,
				attendee_event_id : attendeeGr.getValue('x_event_id'),
				attendee_first_name : attendeeGr.getValue('x_first_name'),
				attendee_last_name : attendeeGr.getValue('x_last_name'),
				attendee_email : attendeeGr.getValue('x_email'),
				attendee_con_num : attendeeGr.getValue('x_contact_number')
			};
			return JSON.stringify(results);
		}
		else{
			return null;
		}


	},
    type: 'AttendeeUtilsClient'
});
