sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var requestBody = request.body.data;

	if(!requestBody.x_event_date || !requestBody.x_event_name || !requestBody.x_location || !requestBody.x_contact_person || !requestBody.x_contact_number) {
		response.setStatus(400);
		return { error: 'Missing required fields' };
	}

	var _eventGR = new GlideRecord('x_1801001_event_ma_event');
	_eventGR.initialize();
	_eventGR.x_event_date = requestBody.x_event_date;
	_eventGR.x_event_name = requestBody.x_event_name;
	_eventGR.x_location = requestBody.x_location;
	_eventGR.x_event_date = requestBody.x_event_date;
	_eventGR.x_contact_number = requestBody.x_contact_number;
	if(requestBody.x_email) _eventGR.u_email = requestBody.x_email;
	if(requestBody.x_assigned_planner) _eventGR.x_assigned_planner = requestBody.x_assigned_planner;

	var sysId = _eventGR.insert();

	if(!sysId) {
		response.setStatus(500);
		return { error: 'Failed to create patient record' };
	}

	response.setStatus(201);
    return {
        message: 'Event record created successfully',
        sys_id: sysId
    };

})(request, response);
