sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var requestBody = request.body.data;

    if (!requestBody.x_event_id || !requestBody.x_first_name || !requestBody.x_last_name || !requestBody.x_contact_number) {
        response.setStatus(400);
        return { error: 'Missing required fields' };
    }

    var _attendeeGR = new GlideRecord('x_1801001_event_ma_attendee');
    _attendeeGR.initialize();

    var _eventGR = new GlideRecord('x_1801001_event_ma_event');
    if (_eventGR.get(requestBody.x_event_id)) {

        _attendeeGR.x_event_id = _eventGR.getUniqueValue();
        _attendeeGR.x_first_name = requestBody.x_first_name;
        _attendeeGR.x_last_name = requestBody.x_last_name;
        if(requestBody.x_email) _attendeeGR.x_email = requestBody.x_email;
        _attendeeGR.x_contact_number = requestBody.x_contact_number;

        var attendeeSysId = _attendeeGR.insert();

        if (!attendeeSysId) {
            response.setStatus(500);
            return { error: 'Failed to create attendee record' };
        }

        response.setStatus(201);
        return {
            message: 'Attendee registered successfully',
            sys_id: attendeeSysId
        };

    } else {
        response.setStatus(400);
        return { error: 'Invalid Event ID' };
    }

})(request, response);
