sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var requestBody = request.body.data;

	if(!requestBody.x_event_id || !requestBody.x_task_name || !requestBody.x_assigned_to || !requestBody.x_due_date) {
		response.setStatus(400);
		return { error: 'Missing required fields' };
	}

	var _taskGR = new GlideRecord('x_1801001_event_ma_task');
	_taskGR.initialize();

	var _eventGR = new GlideRecord('x_1801001_event_ma_event');
	if(_eventGR.get(requestBody.x_event_id)) {
		var _userGR = new GlideRecord('sys_user');
		_userGR.addQuery('user_name', requestBody.x_assigned_to);
		_userGR.query();
		if(_userGR.next()) {
			_taskGR.x_event_id = _eventGR.getUniqueValue();
			_taskGR.x_task_name = requestBody.x_task_name;
			_taskGR.x_due_date = requestBody.x_due_date;
			_taskGR.x_assigned_to = _userGR.getUniqueValue();

			var sysId = _taskGR.insert();

			if(!sysId) {
				response.setStatus(500);
				return { error: 'Failed to create task record' };
			}

			response.setStatus(201);
			return {
				message: 'Task record created successfully',
				sys_id: sysId
			};
		} else {
			response.setStatus(400);
			return { error: 'Invalid Assigned Id' };
		}

	} else {
		response.setStatus(400);
		return { error: 'Invalid Event Id' };
	}


})(request, response);
